import React, { useContext } from "react";
import { useObserver } from "mobx-react";
import styled from "styled-components";

const TodoForm = styled.form`
  display: flex;
  align-items: center;
  justify-content: space-between;
  width: 30rem;
  padding: 20px;
  color: white;
  margin-bottom: 10px;
  background-color: #414745;
  border-radius: 10px;
  text-decoration: none;

  p{
    margin: 0px 10px;
  }
`;

const Pending = styled.div`
   background-color: #ff9900c7;
   padding: 5px 10px;
   border-radius: 5px;
}
`;

const Done = styled.div`
   background-color: #0ad752;;
   padding: 5px 10px;
   border-radius: 5px;
}
`;

export const TaskList = ({ storeContext }) => {
  const store = useContext(storeContext);

  return useObserver(() => (
    <>
      {store.tasks.map(task => (
        <TodoForm
           key={task.id}
           name="todoList"
           onSubmit={e => {
             store.changeState(task.id);
             console.log(task.id);
             e.preventDefault();
           }}
         >
        <div>
          <p>
            {task.name}
          </p>
        </div>
          <div>{task.state === true ? <Done>Done</Done> : <Pending>Pending</Pending>}</div>
          <div>
          <button type="submit">Make as complete</button>
          </div>
          </TodoForm>
      ))}
    </>
  ));
};
